// This package is modified from https://github.com/vejnar/nwalgo
package align

import (
	"fmt"
	"testing"
)

func TestNW(t *testing.T) {
	tests := [][]string{
		{"GATTACA", "GCATGCG"},
		{"ATGTTAT", "ATCGTAC"},

		// {
		// 	"",
		// 	"",
		// },
	}

	var a, b []byte
	alg := NewAligner(&AlignOptions{
		MatchScore:     1,
		MisMatchScore:  -1,
		GapScore:       -1,
		SaveAlignments: true,
		SaveMatrix:     true,
	})
	for _, test := range tests {
		a = []byte(test[0])
		b = []byte(test[1])

		r := alg.Global(a, b)

		fmt.Printf("%s", r.Matrix)
		fmt.Printf("matches: %d, gaps: %d, len: %d, identity: %.2f%%\n",
			r.Matches, r.Gaps, r.Len, float64(r.Matches)/float64(r.Len)*100)
		fmt.Printf("%s\n", r.AlignA)
		fmt.Printf("%s\n", r.AlignM)
		fmt.Printf("%s\n", r.AlignB)

		RecycleAlignResult(r)
	}
}
